mapper.select:
SELECT * FROM MyKeyword
	WHERE U_NUM = #{u_num}
		AND STATUS = #{status}
		 AND KEYWORD = #{keyword}

mapper.update:
UPDATE MYKEYWORD
SET M_COUNT = M_COUNT + 1
WHERE
U_NUM = #{U_NUM}
AND
KEYWORD = #{keyword}

mapper.delete:
DELETE FROM MyKeyword
WHERE
U_NUM = #{ u_num}
AND
KEYWORD = #{keyword}
