mapper.select:
SELECT * FROM
(SELECT rownum as r ,USERS.* FROM USERS WHERE U_ROLE='ROLE_USER')
WHERE r between #{param1} and #{param2}

SELECT * FROM
(SELECT rownum as r ,USERS.* FROM USERS WHERE U_ROLE='ROLE_READY')
WHERE r between #{param1} and #{param2}

mapper.update:
UPDATE USERS set
	U_ROLE = #{u_role}
WHERE U_NUM = #{u_num}
